import React, { useState } from 'react';
import { View, Text, TouchableOpacity, TextInput, ScrollView } from 'react-native';
import { Camera } from 'expo-camera';
import { SafeAreaView } from 'react-native-safe-area-context';
import { Topic, RecordingState } from './src/types';

const topics: Topic[] = [
  {
    id: 'indoor',
    name: 'Indoor',
  },
  {
    id: 'outdoor',
    name: 'Outdoor',
    subcategories: [
      { id: 'household', name: 'Household' },
      { id: 'office', name: 'Office/Workspace' },
      { id: 'retail', name: 'Retail & Commercial' },
    ]
  },
  {
    id: 'transportation',
    name: 'Transportation/Navigation'
  },
  {
    id: 'specialized',
    name: 'Specialized Tasks'
  }
];

function TopicSelector({ onSelect }: { onSelect: (topic: Topic) => void }) {
  return (
    <ScrollView style={styles.container}>
      <Text style={styles.title}>Select topic</Text>
      {topics.map((topic) => (
        <View key={topic.id}>
          <TouchableOpacity
            style={styles.topicButton}
            onPress={() => onSelect(topic)}
          >
            <Text style={styles.topicButtonText}>{topic.name}</Text>
          </TouchableOpacity>
          {topic.subcategories?.map((sub) => (
            <TouchableOpacity
              key={sub.id}
              style={[styles.topicButton, styles.subTopicButton]}
              onPress={() => onSelect(sub)}
            >
              <Text style={styles.topicButtonText}>{sub.name}</Text>
            </TouchableOpacity>
          ))}
        </View>
      ))}
    </ScrollView>
  );
}

export default function App() {
  const [selectedTopic, setSelectedTopic] = useState<Topic | null>(null);
  const [recordingState, setRecordingState] = useState<RecordingState>('idle');
  const [description, setDescription] = useState('');

  return (
    <SafeAreaView style={styles.container}>
      <View style={styles.header}>
        <Text style={styles.title}>sensusAI</Text>
        <Text style={styles.balance}>100 $SENS</Text>
      </View>

      {!selectedTopic ? (
        <TopicSelector onSelect={setSelectedTopic} />
      ) : (
        <View style={styles.container}>
          <Text style={styles.title}>Selected: {selectedTopic.name}</Text>
          <TouchableOpacity
            style={styles.button}
            onPress={() => setSelectedTopic(null)}
          >
            <Text style={styles.buttonText}>Back to Topics</Text>
          </TouchableOpacity>
        </View>
      )}
    </SafeAreaView>
  );
}

const styles = {
  container: {
    flex: 1,
    padding: 16,
    backgroundColor: '#fff',
  },
  header: {
    flexDirection: 'row' as const,
    alignItems: 'center' as const,
    justifyContent: 'space-between' as const,
    marginBottom: 16,
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold' as const,
    marginBottom: 16,
  },
  balance: {
    fontSize: 16,
    fontWeight: '500' as const,
  },
  button: {
    backgroundColor: '#3b82f6',
    padding: 12,
    borderRadius: 8,
    alignItems: 'center' as const,
    marginVertical: 8,
  },
  buttonText: {
    color: '#fff',
    fontSize: 16,
    fontWeight: '500' as const,
  },
  topicButton: {
    backgroundColor: '#fff',
    padding: 12,
    borderRadius: 8,
    borderWidth: 1,
    borderColor: '#e5e7eb',
    marginVertical: 4,
  },
  subTopicButton: {
    marginLeft: 16,
  },
  topicButtonText: {
    fontSize: 16,
  },
};
